
public class NestedIfState {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20;
				int b = 30;
				if(a == 20) {
					System.out.println("this satisfies the first condition");
					if (b == 20) {
						System.out.println("a = 20 and b = 30");
					}
				}

	}

}
